import React, { createContext, useContext, useReducer, useEffect } from 'react';

const UserContext = createContext();

const initialState = {
  user: {
    name: 'Sarah',
    email: 'sarah@example.com',
    emergencyFundTarget: 6000,
    emergencyFundCurrent: 600,
    monthlyBudget: 3000,
    streak: 3,
    level: 2,
    selectedContentCreator: null,
  },
  spending: {
    currentMonth: 2450,
    categories: {
      food: { spent: 450, budget: 400, status: 'danger' },
      transport: { spent: 200, budget: 250, status: 'safe' },
      entertainment: { spent: 300, budget: 200, status: 'danger' },
      utilities: { spent: 150, budget: 150, status: 'safe' },
      shopping: { spent: 200, budget: 300, status: 'safe' },
      healthcare: { spent: 100, budget: 100, status: 'safe' },
      other: { spent: 1050, budget: 600, status: 'danger' }
    }
  },
  goals: [
    {
      id: 1,
      name: 'Weekend Trip',
      targetAmount: 500,
      currentAmount: 250,
      priority: 'high',
      category: 'travel',
      deadline: '2024-03-15'
    },
    {
      id: 2,
      name: 'Emergency Fund',
      targetAmount: 6000,
      currentAmount: 600,
      priority: 'high',
      category: 'savings',
      deadline: '2024-06-01'
    }
  ],
  subscriptions: [
    {
      id: 1,
      name: 'Netflix',
      cost: 15.99,
      status: 'active',
      nextBilling: '2024-02-15',
      category: 'entertainment'
    },
    {
      id: 2,
      name: 'Spotify Premium',
      cost: 9.99,
      status: 'active',
      nextBilling: '2024-02-10',
      category: 'entertainment'
    },
    {
      id: 3,
      name: 'Gym Membership',
      cost: 49.99,
      status: 'active',
      nextBilling: '2024-02-01',
      category: 'health'
    },
    {
      id: 4,
      name: 'Free Trial App',
      cost: 0,
      status: 'free_trial',
      nextBilling: '2024-02-20',
      category: 'productivity'
    }
  ],
  notifications: [
    {
      id: 1,
      type: 'behavioral_nudge',
      message: "FiFi noticed you usually order out Friday nights. Want to try saving $20 this time?",
      timestamp: new Date().toISOString(),
      read: false
    }
  ]
};

function userReducer(state, action) {
  switch (action.type) {
    case 'UPDATE_SPENDING':
      return {
        ...state,
        spending: {
          ...state.spending,
          ...action.payload
        }
      };

    case 'ADD_GOAL':
      return {
        ...state,
        goals: [...state.goals, action.payload]
      };

    case 'UPDATE_GOAL':
      return {
        ...state,
        goals: state.goals.map(goal => 
          goal.id === action.payload.id ? { ...goal, ...action.payload } : goal
        )
      };

    case 'UPDATE_EMERGENCY_FUND':
      return {
        ...state,
        user: {
          ...state.user,
          emergencyFundCurrent: action.payload
        }
      };

    case 'UPDATE_STREAK':
      return {
        ...state,
        user: {
          ...state.user,
          streak: action.payload
        }
      };

    case 'SET_CONTENT_CREATOR':
      return {
        ...state,
        user: {
          ...state.user,
          selectedContentCreator: action.payload
        }
      };

    case 'SET_USER_PROFILE':
      return {
        ...state,
        user: {
          ...state.user,
          ...action.payload,
          emergencyFundTarget: action.payload.emergencyFundTarget || state.user.emergencyFundTarget,
          monthlyBudget: action.payload.monthlyBudget || state.user.monthlyBudget
        }
      };

    case 'ADD_NOTIFICATION':
      return {
        ...state,
        notifications: [action.payload, ...state.notifications]
      };

    case 'MARK_NOTIFICATION_READ':
      return {
        ...state,
        notifications: state.notifications.map(notif => 
          notif.id === action.payload ? { ...notif, read: true } : notif
        )
      };

    default:
      return state;
  }
}

export function UserProvider({ children }) {
  const [state, dispatch] = useReducer(userReducer, initialState);

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      // Simulate spending updates
      const randomCategory = Object.keys(state.spending.categories)[
        Math.floor(Math.random() * Object.keys(state.spending.categories).length)
      ];

      if (Math.random() > 0.7) {
        dispatch({
          type: 'UPDATE_SPENDING',
          payload: {
            categories: {
              ...state.spending.categories,
              [randomCategory]: {
                ...state.spending.categories[randomCategory],
                spent: state.spending.categories[randomCategory].spent + Math.floor(Math.random() * 20)
              }
            }
          }
        });
      }
    }, 30000); // Every 30 seconds

    return () => clearInterval(interval);
  }, [state.spending.categories]);

  const value = {
    state,
    dispatch,
      actions: {
    updateSpending: (payload) => dispatch({ type: 'UPDATE_SPENDING', payload }),
    addGoal: (goal) => dispatch({ type: 'ADD_GOAL', payload: { ...goal, id: Date.now() } }),
    updateGoal: (payload) => dispatch({ type: 'UPDATE_GOAL', payload }),
    updateEmergencyFund: (amount) => dispatch({ type: 'UPDATE_EMERGENCY_FUND', payload: amount }),
    updateStreak: (streak) => dispatch({ type: 'UPDATE_STREAK', payload: streak }),
    setContentCreator: (creator) => dispatch({ type: 'SET_CONTENT_CREATOR', payload: creator }),
    setUserProfile: (profile) => dispatch({ type: 'SET_USER_PROFILE', payload: profile }),
    addNotification: (notification) => dispatch({ 
      type: 'ADD_NOTIFICATION', 
      payload: { ...notification, id: Date.now(), timestamp: new Date().toISOString(), read: false }
    }),
    markNotificationRead: (id) => dispatch({ type: 'MARK_NOTIFICATION_READ', payload: id })
  }
  };

  return (
    <UserContext.Provider value={value}>
      {children}
    </UserContext.Provider>
  );
}

export function useUser() {
  const context = useContext(UserContext);
  if (!context) {
    throw new Error('useUser must be used within a UserProvider');
  }
  return context;
}
